function convert_time(time)
local znak = ''
if time < 0 then znak = '-' end
time = math.abs(time)
local hours = math.floor(time/3600) local remaining = time % 3600 local minutes = math.floor(remaining/60) local answer = znak.. hours..' godzin '..minutes..' minut' return answer end
function predykcje_astro_imp()
local czas, delta, now_delta, pelnia_delta, swieto_delta local dzien, miesiac, godzina, am_pm, zachod local now, pelnia, swieto, swieto_nazwa local am_pm_fix = 0 local poludnie_fix = 0
miesiac = 3 for i=1,#kalendarz_imp_miesiac_txt,1 do if string.find(kalendarz_astro, kalendarz_imp_miesiac_txt[i]) then miesiac = i break end end
for i=1,#kalendarz_am,1 do if string.find(kalendarz_astro, kalendarz_am[i]) then am_pm = 0 break end end
if string.find(kalendarz_astro, kalendarz_am[5]) then godzina = 0 end
for i=1,#kalendarz_pm,1 do if string.find(kalendarz_astro, kalendarz_pm[i]) then am_pm = 1 if i == 4 then am_pm_fix = 1 end if i == 5 then poludnie_fix = 1 godzina = 12 break end break end end
for i=1,#kalendarz_godzina,1 do if string.find(kalendarz_astro, kalendarz_godzina[i]) then godzina = i break end end
delta = 0
if am_pm == 0 then delta = godzina*120 end if am_pm == 1 then delta = (godzina+12)*120 end
if am_pm == 1 and godzina == 12 then delta = 0 end if am_pm == 1 and godzina == 12 and poludnie_fix == 1 then delta = (12*120) end if am_pm == 1 and godzina == 1 and am_pm_fix == 1 then delta = (1*120) end if am_pm == 1 and godzina == 2 and am_pm_fix == 1 then delta = (2*120) end if am_pm == 1 and godzina == 3 and am_pm_fix == 1 then delta = (3*120) end if am_pm == 1 and godzina == 4 and am_pm_fix == 1 then delta = (4*120) end
for i=#kalendarz_imp_dni_liczebniki,1,-1 do if string.find(kalendarz_astro, kalendarz_imp_dni_liczebniki[i]) then dzien=i+kalendarz_imp_delta_dni[miesiac] break end end
for i=1,#kalendarz_imp_swieta_nazwydni,1 do if string.find(kalendarz_astro, kalendarz_imp_swieta_nazwydni[i]) then dzien=kalendarz_imp_swieta[i] break end end
for i=1,#kalendarz_imp_now,1 do if dzien <= kalendarz_imp_now[i] then now=kalendarz_imp_now[i] zachod=kalendarz_imp_zachod[i] break else now=kalendarz_imp_now[1] zachod=kalendarz_imp_zachod[1] end end
for i=1,#kalendarz_imp_pelnia,1 do if dzien <= kalendarz_imp_pelnia[i] then pelnia=kalendarz_imp_pelnia[i] break else pelnia=kalendarz_imp_pelnia[1] end end
for i=1,#kalendarz_imp_swieta,1 do if dzien <= kalendarz_imp_swieta[i] then swieto=kalendarz_imp_swieta[i] swieto_nazwa=kalendarz_imp_swieta_nazwy[i] break else swieto=kalendarz_imp_swieta[1] swieto_nazwa=kalendarz_imp_swieta_nazwy[1] end end
czas = getEpoch()
if now >= dzien then now_delta = (now-dzien)*2880-delta+(zachod*120) else now_delta = (400+kalendarz_imp_now[1]-dzien)*2880-delta+(zachod*120) end
if pelnia >= dzien then pelnia_delta = (pelnia-dzien)*2880-delta else pelnia_delta = (400+kalendarz_imp_pelnia[1]-dzien)*2880-delta end
if swieto >= dzien then swieto_delta = (swieto-dzien)*2880-delta else swieto_delta = (400+kalendarz_imp_swieta[1]-dzien)*2880-delta end
echo("Predykcje wydarzen astronomicznych wg kalendarza imperialnego:\n") echo("==============================================================\n\n")
echo("Aktualny czas : ") print(os.date("%c", czas)) echo("\n")
echo("Najblizszy now : ") print(os.date("%c", czas+now_delta)) echo(" (czyli za okolo " .. convert_time(now_delta) .. " czasu RL)\n") echo("Tego dnia zachod slonca o godzinie " .. zachod .. " czasu arkadyjskiego\n\n")
echo("Najblizsza pelnia : ") print(os.date("%c", czas+pelnia_delta)) echo(" (czyli za okolo " .. convert_time(pelnia_delta) .. " czasu RL)\n\n")
echo("Najblizsze swieto : ") print(os.date("%c", czas+swieto_delta)) echo(" (czyli za okolo " .. convert_time(swieto_delta) .. " czasu RL)\n") echo("Swieto to : " .. swieto_nazwa .. "\n\n")
echo("==============================================================\n\n")
end
function predykcje_astro_ish()
local czas, delta, pelnia_delta, swieto_delta, festyn_delta local dzien, miesiac, godzina, am_pm local pelnia, swieto, swieto_nazwa, festyn local am_pm_fix = 0 local poludnie_fix = 0
if kal_wariant == 0 then kalendarz_ish_pelnia = {1, 26, 51, 76, 101, 126, 151, 176, 201, 226, 251, 276, 301, 326, 351} end if kal_wariant == nil then kalendarz_ish_pelnia = {1, 31, 61, 91, 121, 151, 181, 211, 241, 271, 301, 331} end if kal_wariant == 1 then kalendarz_ish_pelnia = {1, 26, 51, 76, 101, 126, 151, 176, 201, 226, 251, 276, 301, 326, 351} end if kal_wariant == 2 then kalendarz_ish_pelnia = {16, 41, 66, 91, 116, 141, 166, 191, 216, 241, 266, 291, 316, 341} end if kal_wariant == 3 then kalendarz_ish_pelnia = {6, 31, 56, 81, 106, 131, 156, 181, 206, 231, 256, 281, 306, 331, 356} end if kal_wariant == 4 then kalendarz_ish_pelnia = {21, 46, 71, 96, 121, 146, 171, 196, 221, 246, 271, 296, 321, 346} end if kal_wariant == 5 then kalendarz_ish_pelnia = {11, 36, 61, 86, 111, 136, 161, 186, 211, 236, 261, 286, 311, 336} end if kal_wariant == 6 then kalendarz_ish_pelnia = {1, 31, 61, 91, 121, 151, 181, 211, 241, 271, 301, 331} end
for i=1,#kalendarz_ish_miesiac_txt,1 do if string.find(kalendarz_astro, kalendarz_ish_miesiac_txt[i]) then miesiac = i break else if string.find(kalendarz_astro, kalendarz_ish_swieta_nazwy[i]) then miesiac = i break end end end
if kal_wariant == 0 then wyswietl_warianty_ishtar(miesiac) kal_wariant = 6 return end
for i=1,#kalendarz_am,1 do if string.find(kalendarz_astro, kalendarz_am[i]) then am_pm = 0 break end end
if string.find(kalendarz_astro, kalendarz_am[5]) then godzina = 0 end
for i=1,#kalendarz_pm,1 do if string.find(kalendarz_astro, kalendarz_pm[i]) then am_pm = 1 if i == 4 then am_pm_fix = 1 end if i == 5 then poludnie_fix = 1 godzina = 12 break end break end end
for i=1,#kalendarz_godzina,1 do if string.find(kalendarz_astro, kalendarz_godzina[i]) then godzina = i break end end
delta = 0
if am_pm == 0 then delta = godzina*120 end if am_pm == 1 then delta = (godzina+12)*120 end
if am_pm == 1 and godzina == 12 then delta = 0 end if am_pm == 1 and godzina == 12 and poludnie_fix == 1 then delta = (12*120) end if am_pm == 1 and godzina == 1 and am_pm_fix == 1 then delta = (1*120) end if am_pm == 1 and godzina == 2 and am_pm_fix == 1 then delta = (2*120) end if am_pm == 1 and godzina == 3 and am_pm_fix == 1 then delta = (3*120) end if am_pm == 1 and godzina == 4 and am_pm_fix == 1 then delta = (4*120) end
for i=#kalendarz_ish_dni_liczebniki,1,-1 do if string.find(kalendarz_astro, kalendarz_ish_dni_liczebniki[i]) then dzien=i+kalendarz_ish_delta_dni[miesiac] break end end
for i=1,#kalendarz_ish_swieta_nazwy,1 do if string.find(kalendarz_astro, kalendarz_ish_swieta_nazwy[i]) then dzien=1+kalendarz_ish_delta_dni[miesiac] break end end
for i=1,#kalendarz_ish_pelnia,1 do if dzien <= kalendarz_ish_pelnia[i] then pelnia=kalendarz_ish_pelnia[i] break else pelnia=kalendarz_ish_pelnia[1] end end
for i=1,#kalendarz_ish_swieta,1 do if dzien <= kalendarz_ish_swieta[i] then swieto=kalendarz_ish_swieta[i] swieto_nazwa=kalendarz_ish_swieta_nazwy[i] break else swieto=kalendarz_ish_swieta[1] swieto_nazwa=kalendarz_ish_swieta_nazwy[1] end end
czas = getEpoch()
if pelnia >= dzien then pelnia_delta = (pelnia-dzien)*2880-delta else pelnia_delta = (360+kalendarz_ish_pelnia[1]-dzien)*2880-delta end
if swieto >= dzien then swieto_delta = (swieto-dzien)*2880-delta else swieto_delta = (360+kalendarz_ish_swieta[1]-dzien)*2880-delta end
for i=1,#kalendarz_ish_festyn,1 do if dzien <= (kalendarz_ish_festyn[i]+2) then festyn = kalendarz_ish_festyn[i] break else festyn = kalendarz_ish_festyn[1] end end
if festyn >= dzien then festyn_delta = (festyn-dzien)*2880-delta else festyn_delta = (360+kalendarz_ish_festyn[1]-dzien)*2880-delta end
if dzien <= (festyn+2) then festyn_delta = (festyn-dzien)*2880-delta end
echo("Predykcje wydarzen astronomicznych wg kalendarza Starszego Ludu:\n") echo("================================================================\n\n")
echo("Aktualny czas : ") print(os.date("%c", czas)) echo("\n")
if kal_wariant == 6 then echo("Obliczenia oparte o wariant \"co 30 dni\"\n") else echo("Obliczenia oparte o wariant : " .. kal_wariant .. "\n") end
echo("Poczatek najblizszej pelni : ") print(os.date("%c", czas+pelnia_delta)) echo(" (czyli za okolo " .. convert_time(pelnia_delta) .. " czasu RL)\n\n")
echo("Koniec najblizszej pelni : ") print(os.date("%c", czas+pelnia_delta+5760)) echo(" (czyli za okolo " .. convert_time(pelnia_delta+5760) .. " czasu RL)\n\n")
echo("Najblizsze swieto : ") print(os.date("%c", czas+swieto_delta)) echo(" (czyli za okolo " .. convert_time(swieto_delta) .. " czasu RL)\n") echo("Swieto to : " .. swieto_nazwa .. "\n\n")
echo("Festyn w Eysenlaan\n") echo("Rozpoczecie festynu : ") print(os.date("%c", czas+festyn_delta)) echo(" (czyli za okolo " .. convert_time(festyn_delta) .. " czasu RL)\n") echo("Zakonczenie festynu : ") print(os.date("%c", czas+festyn_delta+8640)) echo(" (czyli za okolo " .. convert_time(festyn_delta+8640) .. " czasu RL)\n")
echo("================================================================\n\n")
end
function predykcje_astro()
if kalendarz_astro then if kalendarz_imp_ish == 0 then predykcje_astro_ish() else predykcje_astro_imp() end else echo("Wyjdz na zewnatrz i sprawdz czas...\n") end
end
